package com.depromeet.watni.supports;

import com.depromeet.watni.domain.attendance.dto.PhotoAttendanceRequestDto;
import com.depromeet.watni.domain.attendance.service.PhotoAttendanceService;
import com.depromeet.watni.domain.conference.domain.Conference;
import com.depromeet.watni.domain.conference.dto.ConferenceRequestDto;
import com.depromeet.watni.domain.conference.service.ConferenceService;
import com.depromeet.watni.domain.group.domain.Group;
import com.depromeet.watni.domain.group.dto.GroupDto;
import com.depromeet.watni.domain.group.service.GroupGenerateService;
import com.depromeet.watni.domain.group.service.GroupService;
import com.depromeet.watni.domain.manager.service.ManagerService;
import com.depromeet.watni.domain.member.MemberDetail;
import com.depromeet.watni.domain.member.domain.Member;
import com.depromeet.watni.domain.member.repository.MemberRepository;
import org.apache.commons.io.FileUtils;
import org.json.JSONObject;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.autoconfigure.restdocs.AutoConfigureRestDocs;
import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.core.io.ClassPathResource;
import org.springframework.core.io.Resource;
import org.springframework.http.MediaType;
import org.springframework.restdocs.payload.JsonFieldType;
import org.springframework.test.context.junit4.SpringRunner;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.ResultActions;

import java.io.IOException;
import java.time.LocalDateTime;
import java.util.Base64;

import static com.depromeet.watni.supports.ApiDocumentUtils.getDocumentRequest;
import static com.depromeet.watni.supports.ApiDocumentUtils.getDocumentResponse;
import static org.springframework.restdocs.headers.HeaderDocumentation.headerWithName;
import static org.springframework.restdocs.headers.HeaderDocumentation.requestHeaders;
import static org.springframework.restdocs.mockmvc.MockMvcRestDocumentation.document;
import static org.springframework.restdocs.mockmvc.RestDocumentationRequestBuilders.get;
import static org.springframework.restdocs.mockmvc.RestDocumentationRequestBuilders.post;
import static org.springframework.restdocs.payload.PayloadDocumentation.*;
import static org.springframework.restdocs.request.RequestDocumentation.parameterWithName;
import static org.springframework.restdocs.request.RequestDocumentation.pathParameters;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;

@RunWith(SpringRunner.class)
@AutoConfigureRestDocs
@SpringBootTest
@AutoConfigureMockMvc
public class AttendanceDocuments {

    @Autowired
    private MockMvc mockMvc;
    @Autowired
    private GroupService groupService;
    @Autowired
    private ConferenceService conferenceService;
    @Autowired
    private MemberRepository memberRepository;
    @Autowired
    private ManagerService managerService;
    @Autowired
    private PhotoAttendanceService photoAttendanceService;
    @Autowired
    private GroupGenerateService groupGenerateService;

    private Group group;
    private Conference conference;
    private Member member;

    @Before
    public void setup() throws IOException {
        LocalDateTime localDateTime = LocalDateTime.now();
        ConferenceRequestDto conferenceRequestDto = new ConferenceRequestDto("testConference", "test", "공덕 창업 허브센터", localDateTime, localDateTime.plusDays(1), null, null, null);
        GroupDto groupDto = GroupDto
                .builder()
                .description("test")
                .groupName("testGroup")
                .build();
        MemberDetail memberDetail = new MemberDetail(memberRepository.findById(1L).get());
        group = groupGenerateService.createGroup(groupDto, memberDetail);
        conference = conferenceService.generateConference(conferenceRequestDto, group);
        member = memberRepository.findById(1L).get();
    }

    @Test
    public void 출석체크() throws Exception {
        Resource resource = new ClassPathResource("depromeet.png");

        byte[] fileContent = FileUtils.readFileToByteArray(resource.getFile());
        String encodedString = Base64.getEncoder().encodeToString(fileContent);

        JSONObject requestBody = new JSONObject();
        requestBody.put("attendanceType", "PHOTO");
        requestBody.put("base64Image", encodedString);

        ResultActions result = this.mockMvc.perform(
                post("/api/group/{groupId}/conference/{conferenceId}/attendance", group.getGroupId(), conference.getConferenceId())
                        .header("Authorization", ApiDocumentUtils.getAuthorizationHeader(this.mockMvc))
                        .contentType(MediaType.APPLICATION_JSON)
                        .content(requestBody.toString())
        ).andExpect(status().isCreated());

        result.andExpect(status().isCreated())
                .andDo(document("POST_photoAttendance",
                        getDocumentRequest(),
                        getDocumentResponse(),
                        requestHeaders(
                                headerWithName("Authorization").description("user accessToken")
                        ),
                        pathParameters(
                                parameterWithName("groupId").description("group Id"),
                                parameterWithName("conferenceId").description("conference Id")
                        ),
                        requestFields(
                                fieldWithPath("attendanceType").description("only support PHOTO"),
                                fieldWithPath("base64Image").description("base64 encoded image")
                        ),
                        responseFields(
                                fieldWithPath("attendanceId").type(JsonFieldType.NUMBER).description("attendance PK"),
                                fieldWithPath("name").type(JsonFieldType.STRING).description("user name"),
                                fieldWithPath("attendanceType").type(JsonFieldType.STRING).description("attendance Type, now is only PHOTO support"),
                                fieldWithPath("attendanceStatus").type(JsonFieldType.STRING).description("attendance status"),
                                fieldWithPath("attendanceAt").type(JsonFieldType.NUMBER).description("attendance At"),
                                fieldWithPath("imageUrl").type(JsonFieldType.STRING).description("upload image url")
                        )
                ));
    }

    @Test
    public void 출석체크_조회() throws Exception {
        PhotoAttendanceRequestDto photoAttendanceRequestDto = new PhotoAttendanceRequestDto();
        photoAttendanceRequestDto.setBase64Image("");
        photoAttendanceService.createAttendance(photoAttendanceRequestDto, conference, member);

        ResultActions result = this.mockMvc.perform(
                get("/api/group/{groupId}/conference/{conferenceId}/attendances", group.getGroupId(), conference.getConferenceId())
                        .header("Authorization", ApiDocumentUtils.getAuthorizationHeader(this.mockMvc))
                        .contentType(MediaType.APPLICATION_JSON)
        ).andExpect(status().isOk());

        result.andExpect(status().isOk())
                .andDo(document("GET_attendances",
                        getDocumentRequest(),
                        getDocumentResponse(),
                        requestHeaders(
                                headerWithName("Authorization").description("user accessToken")
                        ),
                        pathParameters(
                                parameterWithName("groupId").description("group Id"),
                                parameterWithName("conferenceId").description("conference Id")
                        ),
                        responseFields(
                                fieldWithPath("[].attendanceId").type(JsonFieldType.NUMBER).description("attendance PK"),
                                fieldWithPath("[].name").type(JsonFieldType.STRING).description("user name"),
                                fieldWithPath("[].attendanceType").type(JsonFieldType.STRING).description("attendance Type, now is only PHOTO support"),
                                fieldWithPath("[].attendanceStatus").type(JsonFieldType.STRING).description("attendance status"),
                                fieldWithPath("[].attendanceAt").type(JsonFieldType.NUMBER).description("attendance At"),
                                fieldWithPath("[].imageUrl").type(JsonFieldType.STRING).description("upload image url")
                        )
                ));
    }
}
